import React from "react";
import { Box, useColorMode } from "@chakra-ui/core";

type CardProps = {
  children: object;
  gridArea: string;
};

const Card = ({ children, gridArea }: CardProps) => {
  const { colorMode } = useColorMode();
  return (
    <Box
      bg={`mode.${colorMode}.card`}
      p={4}
      rounded="sm"
      h="100%"
      gridArea={gridArea}
    >
      {children}
    </Box>
  );
};

export default Card;
