import React from "react";
import { Stack, Text, Badge } from "@chakra-ui/core";

export enum statusBadge {
  inProgress = "In Progress",
  success = "Success",
  failed = "Failed",
}

type TestProps = {
  branchName: string;
  date: string;
  status: statusBadge;
};

export const Test = ({ branchName, date, status }: TestProps) => {
  return (
    <>
      <Stack isInline>
        <Text>{branchName}</Text>
        <Badge
          variantColor={
            status === "In Progress"
              ? "yellow"
              : status === "Success"
              ? "cyan"
              : status === "Failed"
              ? "red"
              : null
          }
          fontSize="14px"
          fontWeight={600}
          rounded="sm"
          padding="0px 4px"
          minH="auto"
          mb={3}
          textTransform="none"
        >
          {status}
        </Badge>
      </Stack>
      <Text>{date}</Text>
    </>
  );
};
