import React from "react";
import { Box, useColorMode } from "@chakra-ui/core";

const Card = ({ children, props }) => {
  const { colorMode } = useColorMode();
  return (
    <Box bg={`mode.${colorMode}.card`} p={4} rounded="sm" h="100%" {...props}>
      {children}
    </Box>
  );
};

export default Card;
