import React from "react";
import {
  Box,
  useColorMode,
  Heading,
  Divider,
  Link,
  Icon,
  Stack,
} from "@chakra-ui/core";

type CardProps = {
  children: object;
  gridArea?: string;
  heading?: string;
  pageLink?: string;
  h?: string;
};

const Card = ({ children, gridArea, heading, pageLink, h }: CardProps) => {
  const { colorMode } = useColorMode();
  return (
    <Box
      bg={`mode.${colorMode}.card`}
      p={4}
      rounded="sm"
      h={h}
      gridArea={gridArea}
      overflow="hidden"
    >
      {pageLink ? (
        <Link
          href={pageLink}
          color={`mode.${colorMode}.title`}
          _hover={{ color: `mode.${colorMode}.titleHover` }}
        >
          <Stack isInline justify="space-between">
            <Heading
              as="h2"
              fontSize="normal"
              letterSpacing="wide"
              lineHeight="normal"
              fontWeight={800}
            >
              {heading}
            </Heading>
            <Icon name="arrow-forward" />
          </Stack>
          <Divider borderColor={`mode.${colorMode}.icon`} />
        </Link>
      ) : (
        <Box>
          {heading ? (
            <Heading
              as="h2"
              color={`mode.${colorMode}.title`}
              fontSize="normal"
              lineHeight="normal"
              letterSpacing="wide"
              fontWeight={800}
              pb={2}
            >
              {heading}
            </Heading>
          ) : null}
          <Divider borderColor={`mode.${colorMode}.icon`} />
        </Box>
      )}

      {children}
    </Box>
  );
};

export default Card;
