import React from "react";
import { Box, useColorMode, Heading, Divider } from "@chakra-ui/core";

type CardProps = {
  children: object;
  gridArea: string;
  heading?: string;
  pageLink?: string;
};

const Card = ({ children, gridArea, heading, pageLink }: CardProps) => {
  const { colorMode } = useColorMode();
  return (
    <Box
      bg={`mode.${colorMode}.card`}
      p={4}
      rounded="sm"
      h="100%"
      gridArea={gridArea}
      overflow="scroll"
    >
      {pageLink ? (
        <Box pos="sticky" top={0} bg={`mode.${colorMode}.card`}>
          {heading ? (
            <Heading
              as="h2"
              color={`mode.${colorMode}.title`}
              fontSize="normal"
              letterSpacing="wide"
              fontWeight={800}
            >
              {heading}
            </Heading>
          ) : null}
          <Divider />
        </Box>
      ) : null}

      {children}
    </Box>
  );
};

export default Card;
