import React from "react";
import { Stack, Text, Badge, Box } from "@chakra-ui/core";

export enum statusBadge {
  inProgress = "In Progress",
  success = "Success",
  failed = "Failed",
}

type TestProps = {
  branchName: string;
  date: string;
  status: statusBadge;
};

export const Test = ({ branchName, date, status }: TestProps) => {
  return (
    <Box my={3} borderBottom="1px solid gray.500">
      <Stack isInline>
        <Text fontWeight={600} lineHeight="normal">
          {branchName}
        </Text>
        <Badge
          variantColor={
            status === "In Progress"
              ? "yellow"
              : status === "Success"
              ? "cyan"
              : status === "Failed"
              ? "red"
              : null
          }
          // fontSize="xs"
          fontWeight={600}
          rounded="sm"
          padding="0px 4px"
          minH="auto"
          mb={3}
          textTransform="none"
        >
          {status}
        </Badge>
      </Stack>
      <Text>{date}</Text>
    </Box>
  );
};
