import React from "react";
import { Stack, useColorMode } from "@chakra-ui/core";

const Layout = ({ children }) => {
  const { colorMode } = useColorMode();
  return (
    <Stack
      minH="100vh"
      p={6}
      spacing={0}
      background={`mode.${colorMode}.background`}
    >
      {children}
    </Stack>
  );
};

export default Layout;
